import React from "react";
import { styled } from "styled-components";
import member from "../sookmut";
import { useTheme } from "./contexts/PartContext";

const MemberList = () => {
  const { part } = useTheme();
  const memberlist = part
    ? member.filter((member) => member.part === part)
    : member;

  return (
    <List>
      {memberlist.map((mem) => (
        <Item>
          <div className="name">ü¶Å {mem.name}</div>
          <div className={mem.role === "ÏïÑÍ∏∞ÏÇ¨Ïûê" ? "baby" : "adult"}>
            {mem.role}
          </div>
        </Item>
      ))}
    </List>
  );
};

export default MemberList;

const List = styled.div`
  width: 100%;
  padding: 50px 100px;
`;
const Item = styled.div`
  display: flex;
