  margin-bottom: 10px;
  align-items: center;
  .baby {
    width: 60px;
    color: white;
    background-color: #ee7521;
    font-size: 15px;
    font-weight: 700;
    padding: 10px 15px;
    border-radius: 30px;
    text-align: center;
  }
  .adult {
    width: 60px;
    background-color: #ffe5c5;
    font-size: 15px;
    font-weight: 700;
    padding: 10px 15px;
    border-radius: 30px;
    text-align: center;
  }
`;
